package client.controller.service.impl;import client.controller.service.TestService;import common.util.DataResult;import common.entity.TestEntity;import common.mapper.Test2Mapper;import common.mapper.TestMapper;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * <p> *  服务实现类 * </p> * * @author AcheDc * @since 2022-01-17 */@Servicepublic class TestServiceImpl extends ServiceImpl<TestMapper, TestEntity> implements TestService {    @Autowired    TestMapper testMapper;    @Autowired    Test2Mapper test2Mapper;    @Override    public DataResult getDetail() {        return DataResult.success(testMapper.ListTestDetail());    }    @Override    public DataResult getDetailById(String id) {        return DataResult.success(testMapper.ListTestDetailByColumn("t.id",id));    }}