package com.common;import lombok.extern.slf4j.Slf4j;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.ConfigurableApplicationContext;import org.springframework.core.env.ConfigurableEnvironment;import org.springframework.web.servlet.config.annotation.EnableWebMvc;import springfox.documentation.swagger2.annotations.EnableSwagger2;import java.net.InetAddress;import java.net.UnknownHostException;@SpringBootApplication@EnableSwagger2@EnableWebMvc@Slf4jpublic class ApiApplication {    public static void main(String[] args) throws UnknownHostException {        ConfigurableApplicationContext application = SpringApplication.run(ApiApplication.class, args);        ConfigurableEnvironment env = application.getEnvironment();        log.info("\n----------------------------------------------------------\n\t" +                        "Application '{}' is running! Access URLs:\n\t" +                        "Login: \thttp://{}:{}/login\n\t" +                        "Doc: \thttp://{}:{}/swagger-ui.html\n" +                        "----------------------------------------------------------",                env.getProperty("spring.application.name"),                InetAddress.getLocalHost().getHostAddress(),                env.getProperty("server.port"),                InetAddress.getLocalHost().getHostAddress(),                env.getProperty("server.port"));    }}