package com.common.mapper;import com.common.entity.TestEntity;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;import java.util.Map;/** * <p> *  Mapper 接口 * </p> * * @author AcheDc * @since 2022-01-17 */@Mapperpublic interface TestMapper extends BaseMapper<TestEntity> {    @Select("SELECT t.id,t.name,t2.age,t2.sex FROM `test` as t INNER JOIN `test2` as t2 on t.id = t2.tid")    List<Map<String,Object>> ListTestDetail();    @Select("SELECT t.id,t.name,t2.age,t2.sex FROM `test` as t INNER JOIN `test2` as t2 on t.id = t2.tid WHERE ${column} = #{id}")    List<Map<String,Object>> ListTestDetailByColumn(@Param("column") String column,@Param("id") String id);}