package common.entity;import com.baomidou.mybatisplus.annotation.*;import com.baomidou.mybatisplus.extension.activerecord.Model;import java.io.Serializable;import java.util.Date;import com.fasterxml.jackson.annotation.JsonFormat;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Getter;import lombok.Setter;import lombok.experimental.Accessors;/** * <p> * * </p> * * @author AcheDc * @since 2022-01-17 */@Getter@Setter@Accessors(chain = true)@TableName("test")@ApiModel(value = "TestEntity对象", description = "")public class TestEntity extends Model<TestEntity> {    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    @ApiModelProperty("姓名")    @TableField("`name`")    private String name;    @ApiModelProperty("备注")    @TableField("remark")    private String remark;    @ApiModelProperty("创建日期")    @JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    @TableField(value = "create_time", fill= FieldFill.INSERT)    private Date createTime;    @ApiModelProperty("修改日期")    @JsonFormat(pattern = "yyyy-MM-dd HH:mm:ss", timezone = "GMT+8")    @TableField(value = "update_time", fill = FieldFill.UPDATE)    private Date updateTime;    @Override    public Serializable pkVal() {        return this.id;    }}